mapper.select:
SELECT p.pname,p.pval FROM sys_perm p, sys_role_perm rp, sys_user_role ur
WHERE p.pid = rp.perm_id AND ur.role_id = rp.role_id
AND ur.user_id = #{userId}

mapper.insert:
INSERT INTO sys_perm (pid,pname,ptype,pval,leaf,created) VALUES
(#{p.pid},#{p.pname},#{p.ptype},#{p.pval},#{p.leaf},#{p.created})
ON DUPLICATE KEY UPDATE pname=VALUES(pname),ptype=VALUES(ptype),pval=VALUES(pval),leaf=VALUES(leaf),updated=now();

mapper.update:
UPDATE `sys_perm`
pname=#{perm.pname}, ptype=#{perm.ptype}, pval=#{perm.pval}, parent=#{perm.parent}, updated = #{perm.updated}
WHERE `pid` = #{perm.pid};
